// MonthTile.js
import React from 'react';
import AutumnAnimation from './Animations/AutumnAnimation';
import MonsoonAnimation from './Animations/MonsoonAnimation';
import WinterAnimation from './Animations/WinterAnimation';
import SpringAnimation from './Animations/SpringAnimation';
import SummerAnimation from './Animations/SummerAnimation';
import './Animations/AutumnAnimation.css';
import './Animations/MonsoonAnimation.css';
import './Animations/WinterAnimation.css';
import './Animations/SpringAnimation.css';
import './Animations/SummerAnimation.css';

const MonthTile = ({ month }) => {
    let animation = null;
  
    // Make sure month is a valid string
    if (!month) return null;
  
    if (month === 'September' || month === 'October' || month === 'November') {
      animation = <AutumnAnimation />;
    } else if (month === 'December' || month === 'January' || month === 'February') {
      animation = <WinterAnimation />;
    } else if (month === 'March' || month === 'April' || month === 'May') {
      animation = <MonsoonAnimation />;
    } else if (month === 'June' || month === 'July' || month === 'August') {
      animation = <SummerAnimation />;
    } else if (month === 'March' || month === 'April' || month === 'May') {
      animation = <SpringAnimation />;
    }
  
    return (
      <div className="month-tile">
        <h2>{month}</h2>
        {animation}
      </div>
    );
  };
  

export default MonthTile;
