import React, { useState, useEffect } from 'react';
import './CalendarPage.css';

const CalendarPage = () => {
  const [currentMonth, setCurrentMonth] = useState(new Date().getMonth());
  const [currentYear, setCurrentYear] = useState(new Date().getFullYear());
  const [daysInMonth, setDaysInMonth] = useState([]);

  useEffect(() => {
    // Calculate days in current month
    const daysInCurrentMonth = new Date(currentYear, currentMonth + 1, 0).getDate();
    setDaysInMonth(Array.from({ length: daysInCurrentMonth }, (_, index) => index + 1));
  }, [currentMonth, currentYear]);

  const changeMonth = (direction) => {
    if (direction === 'next') {
      if (currentMonth === 11) {
        setCurrentMonth(0);
        setCurrentYear(currentYear + 1);
      } else {
        setCurrentMonth(currentMonth + 1);
      }
    } else {
      if (currentMonth === 0) {
        setCurrentMonth(11);
        setCurrentYear(currentYear - 1);
      } else {
        setCurrentMonth(currentMonth - 1);
      }
    }
  };

  return (
    <div className="calendar-page">
      <h2>Calendar</h2>
      <div className="calendar-header">
        <button onClick={() => changeMonth('previous')}>Previous</button>
        <h3>{new Date(currentYear, currentMonth).toLocaleString('default', { month: 'long' })} {currentYear}</h3>
        <button onClick={() => changeMonth('next')}>Next</button>
      </div>
      <div className="calendar-grid">
        {daysInMonth.map((day) => (
          <div key={day} className="calendar-day">
            {day}
          </div>
        ))}
      </div>
    </div>
  );
};

export default CalendarPage;
