// src/components/LoginForm.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LoginForm.css';

const LoginForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const validate = () => {
    const errors = {};
    if (username.length < 3) {
      errors.username = 'Username must be at least 3 characters long';
    }
    const passwordPattern = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*\W).{8,}$/;
    if (!passwordPattern.test(password)) {
      errors.password = 'Password must be at least 8 characters long and include a number, a symbol, a lowercase and an uppercase letter';
    }
    setErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validate()) {
      navigate('/calendar'); // Redirect to calendar page
    }
  };

  return (
    <div className="login-container">
      <div className="icon"><i className="fas fa-user"></i></div>
      <h1>LOGIN</h1>
      <form onSubmit={handleSubmit}>
        <div className="input-field">
          <i className="fas fa-user"></i>
          <input
            type="text"
            name="username"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          {errors.username && <span className="error">{errors.username}</span>}
        </div>
        <div className="input-field">
          <i className="fas fa-lock"></i>
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          {errors.password && <span className="error">{errors.password}</span>}
        </div>
        <div className="options">
          <label>
            <input type="checkbox" name="remember" /> Remember me
          </label>
          <button type="button" className="link-button">Forgot password?</button>
        </div>
        <button type="submit" className="btn">Login</button>
      </form>
      <div className="register">
        Not a member? <button type="button" className="link-button">Create account</button>
      </div>
    </div>
  );
};

export default LoginForm;
