// src/components/LoginForm.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LoginForm.css';
import AutumnAnimation from './Animations/AutumnAnimation';
import MonsoonAnimation from './Animations/MonsoonAnimation';
import WinterAnimation from './Animations/WinterAnimation';
import SpringAnimation from './Animations/SpringAnimation';
import SummerAnimation from './Animations/SummerAnimation';

const LoginForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const validate = () => {
    let errors = {};
    if (!username) errors.username = 'Username is required';
    if (!password) errors.password = 'Password is required';
    if (password && !/^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*\W).{8,}$/.test(password)) {
      errors.password = 'Password must be at least 8 characters long and include a number, a symbol, a lowercase and an uppercase letter';
    }
    setErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validate()) {
      navigate('/calendar');
    }
  };

  const handleReset = () => {
    setUsername('');
    setPassword('');
    setErrors({});
  };

  const getSeasonClass = () => {
    const month = new Date().getMonth();
    if (month >= 2 && month <= 4) return 'spring';
    if (month >= 5 && month <= 7) return 'summer';
    if (month >= 8 && month <= 10) return 'autumn';
    if (month >= 11 || month <= 1) return 'winter';
  };

  // src/components/LoginForm.js
const getSeasonAnimation = () => {
    const month = new Date().getMonth();
    if (month >= 2 && month <= 4) return <SpringAnimation />;
    if (month >= 5 && month <= 7) return <SummerAnimation />;
    if (month >= 8 && month <= 10) return <AutumnAnimation />;
    if (month >= 11 || month <= 1) return <WinterAnimation />;
    if (month >= 3 && month <= 5) return <MonsoonAnimation />;  // Monsoon Animation for March-May
  };
  

  return (
    <div className={`login-form ${getSeasonClass()}`}>
      <h2>Login</h2>
      {getSeasonAnimation()}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Username:</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          {errors.username && <span>{errors.username}</span>}
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          {errors.password && <span>{errors.password}</span>}
        </div>
        <button type="submit">Login</button>
        <button type="button" onClick={handleReset}>Reset</button>
      </form>
    </div>
  );
};

export default LoginForm;
