// MonthTile.js
import React from 'react';
import AutumnAnimation from './Animations/AutumnAnimation';
import MonsoonAnimation from './Animations/MonsoonAnimation';
import WinterAnimation from './Animations/WinterAnimation';
import SpringAnimation from './Animations/SpringAnimation';
import SummerAnimation from './Animations/SummerAnimation';
import './Animations/AutumnAnimation.css';
import './Animations/MonsoonAnimation.css';
import './Animations/WinterAnimation.css';
import './Animations/SpringAnimation.css';
import './Animations/SummerAnimation.css';
import './MonthTile.css';   

const MonthTile = ({ month, index, isSelected, onClick }) => 
    { 
        const renderAnimation = () => 
            { 
                switch (month) 
                { 
                    case 'March': 
                    case 'April': 
                    case 'May': 
                        return <SummerAnimation />; 
                    case 'December': 
                    case 'January': 
                    case 'February': 
                        return <WinterAnimation />; 
                    case 'June': 
                    case 'July': 
                        return <SpringAnimation />; 
                    case 'August': 
                    case 'September': 
                        return <AutumnAnimation />; 
                    case 'October': 
                    case 'November': 
                        return <MonsoonAnimation />; 
                    default: 
                        return null; 
                    } 
                }; 
                return ( 
                    <div className={`month-tile ${isSelected ? 'selected' : ''}`} onClick={onClick}>
                         <div className="month-name">{month}</div> 
                         {renderAnimation()} 
                    </div> 
                ); 
            };
export default MonthTile;
