// AutumnAnimation.js
import React from 'react';
import './AutumnAnimation.css';

const AutumnAnimation = () => {
  const createLeaves = () => {
    const leaves = [];
    for (let i = 0; i < 20; i++) {
      const leafStyle = {
        left: `${Math.random() * 100}vw`,
        animationDuration: `${Math.random() * 5 + 5}s`,
        animationDelay: `${Math.random() * 5}s`,
      };
      leaves.push(<div className="leaf" style={leafStyle} key={i}></div>);
    }
    return leaves;
  };

  return <div className="autumn-animation">{createLeaves()}</div>;
};

export default AutumnAnimation;
